import React, { useState, useEffect } from 'react'
import axios from 'axios'

import { Card, Button, Table, Space, Image, Switch, Popconfirm, Tag, Input, Modal } from 'antd';
import { EditOutlined, DeleteOutlined, QuestionCircleOutlined } from '@ant-design/icons'
import './customer.scss'
const Search = Input.Search;

function Customers() {
    const [isLoading, setIsLoading] = useState(false)
    const [customers, setCustomers] = useState([])
    const [records, setRecords] = useState([])
    const [selectedRowKeys, setSelectedRowKeys] = useState([])
    const [selectedRows, setSelectedRows] = useState([])
    const [visible, setVisible] = useState(false)
    const [selected, setSelected] = useState(null)
    const [selectedIsActive, setSelectedIsActive] = useState(true)

    const onEditClick = (customer) => {
        setVisible(true)
        setSelected(customer)
        setSelectedIsActive(customer.isActive)
    }
    const onCancelModal = () => {
        setVisible(false)
        setSelected(null)
        setSelectedIsActive(true)
    }

    useEffect(() => {
        setIsLoading(true)
        axios.get('/customer/getall')
            .then(res => {
                setCustomers(res.data.users);
                setRecords(res.data.users);
                setIsLoading(false)
            })
            .catch(err => {
                setIsLoading(false)
                console.log(err);
            })
    }, [])


    const handleSearch = val => {

        if (val == "")
            setCustomers(records);
        else
            setCustomers(records.filter(x => x.mobile.includes(val)))

    }

    const handleSave=()=>{
        axios.patch(`/customer/update/${selected._id}`,{isActive:selectedIsActive})
        .then(res=>{
            let array = [...customers]
            let index = array.findIndex(u=>u._id === res.data.user._id)
            array[index] = res.data.user
            setCustomers(array)
            onCancelModal()
        })
        .catch(err => {
            console.log(err);
        })
    }



    const rowSelection = {
        selectedRowKeys,
        onChange: (selectedRowKeys, selectedRows) => {

            setSelectedRowKeys(selectedRowKeys);
            setSelectedRows(selectedRows)
        },
    };


    const columns = [

        {
            title: 'Profile',
            dataIndex: 'profilePicture',
            key: 'profilePicture',
            render: profilePicture => <Image
                width={60}

                src={profilePicture ? profilePicture : "data:image/png;base64,"}
                fallback="data:image/png;base64,"
            />
        },
        {
            title: 'Name',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: 'Mobile Number',
            dataIndex: 'mobile',
            key: 'mobile',
        },
        {
            title: 'Email',
            dataIndex: 'email',
            key: 'email',
            render: email =>
                <span>{email ? email : "N/A"}</span>
        },
        {
            title: 'Gender',
            dataIndex: 'gender',
            key: 'gender',
            render: gender =>
                <span>{gender ? gender : "N/A"}</span>,
            filters: [
                { text: 'Male', value: 'male' },
                { text: 'Female', value: 'female' },
                { text: 'Other', value: 'other' },
            ],
            onFilter: (value, record) => record.gender?.includes(value) && record?.gender === value && record.gender?.includes(value)
        },
        {
            title: 'Status',
            dataIndex: 'isActive',
            key: 'isActive',
            render: isActive =>
                <span>{isActive ? <Tag color="green">Active</Tag> : <Tag color="red">Banned</Tag>}</span>,
            filters: [
                { text: 'Active', value: true },
                { text: 'Banned', value: false },
            ],
            onFilter: (value, record) => record.isActive === value
        },
        {
            title: 'Role',
            dataIndex: 'role',
            key: 'role',
            filters: [
                { text: 'User', value: 'user' },
                { text: 'Admin', value: 'admin' },
                { text: 'Super-Admin', value: 'super-admin' },
            ],
            onFilter: (value, record) => record.role.includes(value) && record.role === value && record.role.includes(value)
        },
        {
            title: 'Action',
            key: 'action',
            render: (text, record) => (
                <Space size="middle">
                    <Button disabled={record.role === 'super-admin'} onClick={() => onEditClick(record)} className='d-center' type='primary' icon={<EditOutlined />}></Button>
                    {/* <Popconfirm onConfirm={() => deleteProduct(record._id)} title="Are you sure？" icon={<QuestionCircleOutlined style={{ color: 'red' }} />}>
                        <Button className='d-center' icon={<DeleteOutlined />} danger></Button>
                    </Popconfirm> */}

                </Space>
            ),
        },
    ];
    return (
        <Card title="Customers">
            <Search
                placeholder="Enter Mobile Number"
                onSearch={handleSearch}
                style={{ width: 200 }}
            />
            <Table
                rowKey="_id"
                // rowSelection={rowSelection}
                loading={isLoading}
                columns={columns}
                dataSource={customers}
                pagination={{ defaultPageSize: 10, showSizeChanger: true, pageSizeOptions: ['10', '20', '30'], showQuickJumper: true }}
            />






            <Modal
                title={selected && selected.name}
                centered
                visible={visible}
                onOk={() => handleSave()}
                onCancel={() => onCancelModal()}
                width={800}
                zIndex={1111}
                okText={"Save"}
            >

                {/* <div className='customer_modal'>
                    <div className="card" style={{background:"#4834d4"}}>
                        <strong className="count">10</strong>
                        <span>Orders</span>
                    </div>
                    <div className="card" style={{background:"#22a6b3"}}>
                        <strong className="count">10</strong>
                        <span>Pending</span>
                    </div>
                    <div className="card" style={{background:"#7ed6df"}}>
                        <strong className="count">10</strong>
                        <span>Processing</span>
                    </div>
                    <div className="card" style={{background:"#e056fd"}}>
                        <strong className="count">10</strong>
                        <span>Packed</span>
                    </div>

                </div>
                <div className='customer_modal'>

                    <div className="card" style={{background:"#686de0"}}>
                        <strong className="count">10</strong>
                        <span>Shipped</span>
                    </div>
                    <div className="card" style={{background:"#6ab04c"}}>
                        <strong className="count">10</strong>
                        <span>Delivered</span>
                    </div>
                    <div className="card" style={{background:"#eb4d4b"}}>
                        <strong className="count">10</strong>
                        <span>Cancelled</span>
                    </div>
                    <div className="card" style={{background:"#badc58"}}>
                        <strong className="count">10</strong>
                        <span>Refunded</span>
                    </div>
                </div> */}
                {selected && 
                <div className="customer_modal_info">
                    <table>
                        <tr>
                            <td>Name:</td>
                            <td>{selected.name}</td>
                            <td>Contact No:</td>
                            <td>{selected.contactNumber?selected.contactNumber:"N/A"}</td>
                        </tr>
                        <tr>
                            <td>Mobile No:</td>
                            <td>{selected.mobile}</td>
                            <td>Gender:</td>
                            <td>{selected.gender?selected.gender:"N/A"}</td>
                        </tr>
                        <tr>
                            <td>Email:</td>
                            <td>{selected.email?selected.email:"N/A"}</td>
                            <td>Date of Birth:</td>
                            <td>{selected.birthDate?selected.birthDate:"N/A"}</td>
                        </tr>
                    </table>
                    <hr/>
                    <div className='banned'>
                        <label>Block this user</label>
                    <Switch  checked={!selectedIsActive} onChange={val=>setSelectedIsActive(!val)} />
                    </div>

                </div>
               }
            </Modal>
        </Card>
    )
}

export default Customers
