import React, { useState, useEffect } from 'react';
import "./category.scss"
import {
  Box,
  Container,
  makeStyles,
  IconButton,
  Typography,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button
} from '@material-ui/core';
import { Image, Button as AButton } from 'antd'
import { UploadOutlined } from '@ant-design/icons'

import axios from 'axios'
import EditIcon from '@material-ui/icons/Edit';
import DeleteForeverIcon from '@material-ui/icons/DeleteForever';



import TreeView from '@material-ui/lab/TreeView';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import TreeItem from '@material-ui/lab/TreeItem';
import AddIcon from '@material-ui/icons/Add';

import Backdrop from '@material-ui/core/Backdrop';
import CircularProgress from '@material-ui/core/CircularProgress';
import MediaLibrary from '../../components/MediaLibrary';
import {useSelector,useDispatch} from 'react-redux'
import {setCategories} from '../../actions/generalData'



const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: theme.palette.background.dark,
    minHeight: '100%',
    paddingBottom: theme.spacing(3),
    paddingTop: theme.spacing(3)
  },
  catContainer: {
    width: "500px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "5px 20px"
  }
}));

const CaterogyList = () => {
  const classes = useStyles();
  const {categories,isCategoryLoading} = useSelector(state => state.data)
  const dispatch = useDispatch()

  const [display, setDisplay] = useState(false)
  const [open, setOpen] = React.useState(false);



  const [name, setName] = useState("")
  const [edit, setEdit] = useState(null)
  const [parent, setParent] = useState(null)

  const [categoryImg, setCategoryImg] = useState('')


  const handleClickOpen = (cat) => {

    setParent(cat || null)

    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setEdit(null)
    setParent(null)
    setName('')
    setCategoryImg('')
  };

  const handleEdit = (cat) => {
    setEdit(cat)
    setName(cat.name)
    setCategoryImg(cat.categoryImage)
    handleClickOpen()
  }

  const handleAdd = () => {
    let data = {
      name,
      categoryImage: categoryImg
    }
    if (parent) {
      data.parentId = parent._id
    }
    axios.post('/category/create', data)
      .then(res => {
        if (res.data.success) {
          dispatch(setCategories())
          handleClose()
        }
      })
      .catch(err => {
        err && err.response && alert(err.response.data.error)
      })
  }

  const handleSaveEdit = (cat) => {
    let data = {
      name,
      categoryId: cat._id,
      categoryImage: categoryImg
    }
    axios.post('/category/update', data)
      .then(res => {
        if (res.data.success) {
          dispatch(setCategories())
          handleClose()
        }
      })
      .catch(err => {
        err && err.response && alert(err.response.data.error)
      })

  }

  const handleDelete = (cat) => {

    if (cat.children.length) {
      return alert("Plesae delete sub category first")
    }
    let consent = window.confirm('Are you sure')
    if (!consent) return
    axios.post('/category/delete', { categoryId: cat._id })
      .then(res => {
        if (res.data.success) {
          dispatch(setCategories())
          handleClose()
        }
      })
      .catch(err => {
        err && err.response && alert(err.response.data.error)
      })
  }




  const handleImageselect = (item) => {
    setCategoryImg(item.thumbnailUrl);
    setDisplay(false)
  }


  return (
    <>
      <MediaLibrary display={display} onHide={() => setDisplay(false)} selectCallback={(item) => handleImageselect(item)} />

      <div >

        <Dialog
          open={open}
          onClose={handleClose}
          aria-labelledby="alert-dialog-title"
          aria-describedby="alert-dialog-description"

        >
          <DialogTitle id="alert-dialog-title">Add {parent && "Sub"} Category</DialogTitle>
          <DialogContent>
            <div>
              {
                parent && <span><strong>Parent:</strong>{parent.name}</span>
              }
            </div>

            <div>
              <TextField
                label="Category Name"
                type='text'
                fullWidth
                style={{ width: "300px" }}
                onChange={(e) => setName(e.target.value)}
                value={name}

              />
            </div>

            <div className='input_item_category'>
              <label>Category image <span className='required'>*</span></label>
              <div style={{ width: "100%" }}>
                <AButton className='d-center' onClick={() => setDisplay(true)} type="primary" shape="round" icon={<UploadOutlined />} size={"size"} >Upload image </AButton>


                {
                  categoryImg && <>
                    <div className='image_container'>
                      <span onClick={() => setCategoryImg('')}>X</span>
                      <img src={categoryImg}></img>
                    </div>
                  </>
                }


              </div>

            </div>

          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Cancel
          </Button>
            {
              edit ? <Button onClick={() => handleSaveEdit(edit)} color="primary" autoFocus>
                Edit
          </Button> :
                <Button onClick={() => handleAdd()} color="primary" autoFocus>
                  Add
          </Button>
            }

          </DialogActions>
        </Dialog>
      </div>






      <div
        className={classes.root}
        title="Categories"
      >

        <Container maxWidth={false}>
          <div>
            <Box display="flex" justifyContent="flex-end" >
              <AButton type='primary' onClick={() => handleClickOpen()}>Add Category</AButton>
            </Box>

          </div>
          <Box style={{ background: "white", padding: "30px" }} mt={3}>


            <TreeView

              defaultCollapseIcon={<ExpandMoreIcon />}
              defaultExpandIcon={<ChevronRightIcon />}
            >

              {
                categories?.length > 0 ? categories.map((cat, index) => {
                  return (

                    <TreeItem
                      style={{ marginBottom: "10px" }}
                      key={index}
                      nodeId={cat._id}
                      label={
                        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                          <div className='d_center'>
                            <Image
                              width={50}
                              src={cat.categoryImage}
                              fallback="data:image/png;base64,"
                            />{cat.name}
                          </div>

                          <div>
                            <IconButton onClick={() => handleClickOpen(cat)}><AddIcon color='primary' /></IconButton>
                            <IconButton onClick={() => handleEdit(cat)}><EditIcon /></IconButton>
                            <IconButton onClick={() => handleDelete(cat)}><DeleteForeverIcon style={{ color: "red" }} /></IconButton>
                          </div>
                        </div>
                      }>
                      {
                        cat.children.length > 0 && cat.children.map(child => {
                          return (
                            <TreeItem
                              style={{ borderLeft: "1px solid black", marginBottom: "10px" }}
                              key={child._id}
                              nodeId={child._id}
                              label={
                                <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                                  <div className='d_center'>
                                    <Image
                                      width={50}
                                      src={child.categoryImage}
                                      fallback="data:image/png;base64,"
                                    />{child.name}
                                  </div>
                                  <div>
                                    <IconButton onClick={() => handleClickOpen(child)}><AddIcon color='primary' /></IconButton>
                                    <IconButton onClick={() => handleEdit(child)}><EditIcon /></IconButton>
                                    <IconButton onClick={() => handleDelete(child)}><DeleteForeverIcon style={{ color: "red" }} /></IconButton>
                                  </div>
                                </div>
                              }

                            >
                              {
                                child.children.length > 0 && child.children.map(child2 => {
                                  return (
                                    <TreeItem
                                      style={{ borderLeft: "1px dashed black", marginBottom: "10px" }}
                                      key={child2._id}
                                      nodeId={child2._id}
                                      label={
                                        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                                          <div className='d_center'>
                                            <Image
                                              width={50}
                                              src={child2.categoryImage}
                                              fallback="data:image/png;base64,"
                                            />{child2.name}
                                          </div>
                                          <div><IconButton onClick={() => handleEdit(child2)}><EditIcon /></IconButton>
                                            <IconButton onClick={() => handleDelete(child2)}><DeleteForeverIcon style={{ color: "red" }} /></IconButton>
                                          </div>
                                        </div>
                                      }

                                    >

                                    </TreeItem>
                                  )
                                })
                              }
                            </TreeItem>
                          )
                        })
                      }


                    </TreeItem>

                  )
                }) :
                  <p>No categories found</p>
              }

            </TreeView>




          </Box>
        </Container>
      </div>
      <Backdrop style={{ zIndex: "999999" }} open={isCategoryLoading}>
        <CircularProgress color="inherit" />
      </Backdrop>
    </>
  );
};

export default CaterogyList;
