import React, { useState, useEffect } from 'react'
import { Select, Col, Card, Input, Button, Table, Tag, Space, Image, Switch, Popconfirm, Modal, DatePicker } from 'antd';
import axios from 'axios'
import { useParams } from 'react-router-dom';
const { Option } = Select;



function AddToCamp({params,isModalVisible,handleOk,handleCancel,sendProduct}) {


    const [products, setProducts] = useState([])


    const [categories, setCategories] = useState([])
    const [subCategories, setSubCategories] = useState([])
    const [category, setCategory] = useState('')
    const [subCategory, setSubCategory] = useState('')
    const [brands, setBrands] = useState([])
    const [brand, setBrand] = useState('')
    const [selectedRowKeys, setSelectedRowKeys] = useState([])
    const [selectedRows, setSelectedRows] = useState([])

    const fetchCategories = () => {
        axios.get("/category/getcategory")
            .then(res => {
                setCategories(res.data.categories)
            })
            .catch(err => {
                console.log(err);
            })
    }

    const fetchBrands = () => {
        axios.get('/brand/get')
            .then(res => {
                // console.log(res.data.brands);
                setBrands(res.data.brands)
            })
            .catch(err => {
                err && err.response && console.log(err.response.data)
            })
    }

    const fetchProducts = () => {
        axios.get('/campaign/getproductstoselect/'+params.campaignid)
            .then(res => {
                setProducts(res.data.products)
            })
            .catch(err => {
                err && err.response && console.log(err.response.data)
            })
    }


    useEffect(() => {
        //fetchCategories()
        //fetchBrands()
        fetchProducts()
    }, [isModalVisible])

    //category actions
    useEffect(() => {
        if (category) {
            let filterd = categories.filter(cat => cat._id === category)
            setSubCategories(filterd[0]?.children)
            setSubCategory('')

        } else {
            setSubCategories([])
            setSubCategory('')

        }
    }, [category])




    const rowSelection = {
        selectedRowKeys,
        onChange: (selectedRowKeys, selectedRows) => {

            setSelectedRowKeys(selectedRowKeys);
            setSelectedRows(selectedRows)
        },
    };





    const handleColumnValue = (value, _id, type) => {

        if (type === 'discountvalue') {
            let selected = [...selectedRows]
            let index = selected.findIndex(r => r._id === _id)
            selected[index] = { ...selected[index], campDiscountValue: value }
            setSelectedRows(selected)
        }
        if (type === 'discounttype') {
            let selected = [...selectedRows]
            let index = selected.findIndex(r => r._id === _id)
            selected[index] = { ...selected[index], campDiscountType: value }
            setSelectedRows(selected)
        }
    }

   // console.log(selectedRows);



    const columns = [
        {
            title: 'Name',
            dataIndex: 'name',
            render: (text) => <a>{text}</a>,
        },
        {
            title: 'Thumbnail',
            key: 'thumbnail',
            dataIndex: 'thumbnail',
            render: thumbnail => <Image
                width={30}
                src={thumbnail}
                fallback="data:image/png;base64,"
            />
        },
        {
            title: 'price',
            dataIndex: 'price',
            key: 'price',
            render: price =>
                <span>$ {price}</span>

        },
        {
            title: 'Previous Discount',
            key: 'discount',
            dataIndex: 'discount',
            render: (discount) =>
                discount.value > 0 ?
                    discount.discountType === 'flat' ?
                        <span className="new-price">$ {discount.value}</span> :
                        <span className="new-price">{discount.value} %</span>
                    : <span>$ 0</span>
        },
        {
            title: 'Campaign Discount',
            dataIndex: '_id',
            key: '_id',
            render: _id =>
                <>
                    <Input
                        defaultValue={0}
                        style={{ width: "50%", marginRight: "10px" }}
                        onChange={(e) => handleColumnValue(e.target.value, _id, "discountvalue")}
                        type='number'
                        disabled={!selectedRowKeys.includes(_id)}
                    >

                    </Input>
                    <Select
                        dropdownStyle={{ zIndex: "1111" }}
                        style={{ width: "45%" }}
                        placeholder="Select discount type"
                        onChange={(value) => handleColumnValue(value, _id, "discounttype")}
                        defaultValue={'flat'}
                        //value={discountType}
                        disabled={!selectedRowKeys.includes(_id)}

                    >
                        <Option value="flat">Flat</Option>
                        <Option value="percent">Percent</Option>
                    </Select>
                </>

        },
    ];


    const addToCamp = () => {
        let campaignId = params.campaignid
        selectedRows.map(rows => {
            let data = {
                campaignId: campaignId,
                productId: rows._id,
                value: rows.campDiscountValue,
                discountType: rows.campDiscountType
            }
            axios.patch(`/campaign/addproducttocampaign`, data)
                .then(res => {
                    sendProduct(res.data.product)
                    setProducts(prev=>prev.filter(prod=>prod._id !== res.data.product._id))
                    setSelectedRows(prev=>prev.filter(prod=>prod._id !== res.data.product._id))
                    setSelectedRowKeys(prev=>prev.filter(_id=>_id !== res.data.product._id))
                })
                .catch(err => {
                    err && err.response && console.log(err.response.data)
                })
        })
    }
    return (
        <>









            <Modal
                zIndex={1111}
                title="Basic Modal"
                visible={isModalVisible}
                onOk={handleOk}
                onCancel={handleCancel}
                width={1000}
                style={{ top: "10px" }}
                bodyStyle={{ overflow: "auto", height: "80vh" }}
                footer={[
                    <>
                        <Button onClick={() => handleCancel()} className='submit_btn' type="primary" danger>Cancel</Button>
                        {/* {
                            editCamp ?
                                <Button  onClick={() => updateCamp()} className='submit_btn' type="primary">Update</Button> :
                                <Button loading={isLoading} onClick={() => createCamp()} className='submit_btn' type="primary">Create</Button>
                        } */}

                    </>
                ]}
            >

                <div className='campaign_wrapper'>

                    <Button onClick={() => addToCamp()} disabled={selectedRows.length === 0} type='primary'>Add to campaign</Button>
                    {selectedRows.length > 0 && <strong style={{ marginLeft: "10px" }}>{selectedRows.length} products seleted</strong>}
                    <Table
                        rowSelection={rowSelection}
                        columns={columns}
                        dataSource={products}
                        rowKey="_id"

                    />
                </div>

            </Modal>
        </>
    )
}

export default AddToCamp
